import React from "react";
export default function Tarih(props) {
  const { selectDate, buttonDateIncrease } = props;
  return (
    <div className="tarihSection">
      <h4>Choose a Date</h4>
      <p>Select a new date to view the APOD</p>
      <input
        type="date"
        onChange={(event) => selectDate(event.target.value)}
      ></input>
      <p>OR</p>
      <p>Click the buttons below to move forward / backward in time</p>
      <div className="previousNextDay">
        <button onChange={buttonDateDecrease}>Previous Day</button>
        <button onChange={buttonDateIncrease}>Next Day </button>
      </div>
    </div>
  );
}
