import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./Header";
import Baslik from "./Baslik";
import axios from "axios";

function App() {
  const [APOD, setAPOD] = useState(null);

  let date = new Date();
  const [yil, setYil] = useState(date.getFullYear());
  const [ay, setAy] = useState(date.getMonth() + 1);
  const [gun, setGun] = useState(date.getDate());
  console.log(ay);
  useEffect(() => {
    axios
      .get(
        "https://api.nasa.gov/planetary/apod?api_key=5dqIKcC152afcVcE1kaWF70uoMkXN5WlcXKV0jt5&start_date="
      )
      .then((response) => {
        setAPOD(response.data.url);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <div className="App">
      <Header src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTShI6Pr9VZeVDCFZjvs3DExe9hDwiAW2I7Q&usqp=CAU" />
      <Baslik baslik="NASA Astronomy Picture of the Day (APOD)" />
      <div>
        {APOD !== null ? <img className="APODImg" src={APOD}></img> : null}
      </div>
      <br />
      <select>
        <label>Yıl</label>
        <option>2022</option>
        <option>2021</option>
        <option>2020</option>
      </select>
      <select>
        <label>Ay</label>
        <option>01</option>
        <option>02</option>
        <option>03</option>
      </select>
      <select>
        <label>Gün</label>
        <option>01</option>
        <option>02</option>
        <option>03</option>
      </select>
    </div>
  );
}

export default App;
