import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./Header";
import Baslik from "./Baslik";
import axios from "axios";

function App() {
  let date = new Date();
  let yil = date.getFullYear();
  let ay = date.getMonth() + 1;
  let gun = date.getDay() + 1;

  const [APOD, setAPOD] = useState(null);
  const [year, setYear] = useState(yil);
  const [month, setMonth] = useState(ay);
  const [day, setDay] = useState(gun);

  console.log(year + "-" + month + "-" + day);

  useEffect(() => {
    axios
      .get(
        "https://api.nasa.gov/planetary/apod?api_key=5dqIKcC152afcVcE1kaWF70uoMkXN5WlcXKV0jt5"
      )
      .then((response) => {
        setAPOD(response.data.url);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <div className="App">
      <Header src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTShI6Pr9VZeVDCFZjvs3DExe9hDwiAW2I7Q&usqp=CAU" />
      <Baslik baslik="NASA Astronomy Picture of the Day (APOD)" />
      <div>
        {APOD !== null ? <img className="APODImg" src={APOD}></img> : null}
      </div>
      <br />
    </div>
  );
}

export default App;
