import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./Header";
import Baslik from "./Baslik";
import Tarih from "./Tarih";
import axios from "axios";

function App() {
  const [APOD, setAPOD] = useState(null);
  const [title, setTitle] = useState(null);
  const [copyright, setCopyright] = useState(null);
  const [explanation, setExplanation] = useState(null);
  const [date, setDate] = useState("");

  useEffect(() => {
    axios
      .get(
        "https://api.nasa.gov/planetary/apod?api_key=5dqIKcC152afcVcE1kaWF70uoMkXN5WlcXKV0jt5&date="
      )
      .then((response) => {
        setAPOD(response.data.url);
        setTitle(response.data.title);
        setCopyright(response.data.copyright);
        setExplanation(response.data.explanation);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  function dateChanger(days) {
    let gun = new Date(tarih);
    gun.setDate(gun.getDate() + days);
    let gercekTarih = `${gun.getFullYear()}-${gun.getMonth() +
      1}-${gun.getDate()}`;
    console.log(gercekTarih);
    setTarih(gercekTarih);
  }

  return (
    <div className="App">
      <Header src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTShI6Pr9VZeVDCFZjvs3DExe9hDwiAW2I7Q&usqp=CAU" />
      <Baslik baslik="NASA Astronomy Picture of the Day (APOD)" />
      <div className="NASA-section">
        {APOD !== null ? <img className="APODImg" src={APOD}></img> : null}
        <div className="NASA-texts">
          <h1>{title}</h1>
          <h2>{copyright}</h2>
          <p>{explanation}</p>
        </div>
      </div>
      <br />
      <Tarih changer={dateChanger} />
    </div>
  );
}

export default App;
