import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./Header";
import Baslik from "./Baslik";
import axios from "axios";

function App() {
  const date = new Date();
  const yil = date.getFullYear();
  const ay = date.getMonth();

  console.log(ay);
  const [APOD, setAPOD] = useState(null);
  //const [year, setYear] = useState(2022);
  //const [month, setMonth] = useState(1);
  //const [day, setDay] = useState(1);
  useEffect(() => {
    axios
      .get("https://api.nasa.gov/planetary/apod?api_key=DEMO_KEY&date=")
      .then((response) => {
        setAPOD(response.data.url);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <div className="App">
      <Header src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTShI6Pr9VZeVDCFZjvs3DExe9hDwiAW2I7Q&usqp=CAU" />
      <Baslik baslik="NASA Astronomy Picture of the Day (APOD)" />
      {APOD !== null ? <img className="APODImg" src={APOD}></img> : null}
      <br />
      <button className="previousDay">Previous Day</button>{" "}
      <button className="nextDay">Next Day</button>
    </div>
  );
}

export default App;
