import React from "react";
import {
  StyledTarihContainer,
  StyledTarihInput,
  StyledCurrentDate,
  StyledCurrentDateButton,
} from "./styles/Tarih.styled";

export default function Tarih(props) {
  const { selectDate, resetDate } = props;
  return (
    <StyledTarihContainer>
      <p>Select a new date to view the APOD</p>
      <StyledTarihInput
        type="date"
        onChange={(event) => selectDate(event.target.value)}
      ></StyledTarihInput>
      <StyledCurrentDate>
        <StyledCurrentDateButton onClick={resetDate}>
          See today's APOD
        </StyledCurrentDateButton>
      </StyledCurrentDate>
    </StyledTarihContainer>
  );
}
